mapper.update:
update deal set
oos_status = #{oosStatus},update_time = #{updateTime}
where id = #{id}

update deal
set
inventory_amount = #{inventoryAmount,jdbcType=INTEGER},
vendibility_amount = #{vendibilityAmount,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}
and publish_status = 3
